void Drivetrain::DriveForwardJoystick(Joystick* joy){
	aMotor->Set(joy->GetY()+joy->GetX());
	bMotor->Set(joy->GetY()+joy->GetX());
	cMotor->Set(-1*joy->GetY()+joy->GetX());
	dMotor->Set(-1*joy->GetY()+joy->GetX());
}
